           return (
            <li className="list" key={index}>
              <h2>Nombre: {this.props.name}</h2>
              <p>Descripción: {this.props.description}</p>
              <p>Cantidad: {props.quantity}</p>
              <p>Categoría: {this.props.category}</p>
