import React from "react";
import MediaCard from './MediaCard';
import './App.scss';

const imgURL= 'https://images.unsplash.com/photo-1595119396388-b8822b6c91fc?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80';


const App = () => {
    return (
      <div className="App">
      <MediaCard 
      name='Perica de los Palotes' 
      date='21th of February, 2021'
      img= {imgURL}
      text='La tonta con su tambor y a veces con el cornetín imitaba al pregonero que trataría de desembarazarse de ella ante la risa y el regocijo de todos.'
      likes='1234'
      />
      </div>
    );
}

export default App;
